import type { UserModel } from '@/services/system/user/type';
import { ManOutlined, WomanOutlined, MoreOutlined } from '@ant-design/icons';
import {
  Button,
  Space,
  Tag,
  Image,
  Dropdown,
  type TableProps,
  type MenuProps,
} from 'antd';

/**
 * 获取表格列
 * @param handleEdit 编辑
 * @param handleDetail 详情
 * @param handleMore 更多
 * @returns 表格列
 */
export const getColumns = (
  handleEdit: (record: UserModel) => void,
  handleDetail: (record: UserModel) => void,
  handleMore: (record: UserModel) => MenuProps['items'],
): TableProps<UserModel>['columns'] => [
  {
    dataIndex: 'id',
    title: 'ID',
    key: 'id',
    hidden: true,
  },
  {
    dataIndex: 'username',
    title: '用户名',
    key: 'username',
    width: 80,
    align: 'left',
  },
  {
    dataIndex: 'realName',
    title: '真实名',
    key: 'realName',
    width: 80,
    align: 'left',
  },
  {
    dataIndex: 'sex',
    title: '性别',
    key: 'sex',
    width: 40,
    align: 'center',
    render: (text: number) => {
      return text === 1 ? (
        <ManOutlined className="text-blue-400!" />
      ) : (
        <WomanOutlined className="text-pink-400!" />
      );
    },
  },
  {
    dataIndex: 'avatar',
    title: '头像',
    key: 'avatar',
    width: 80,
    align: 'center',
    render: (text: string) => (
      <Image
        src={text}
        preview={false}
        width={30}
        fallback="data:image/png;base64,"
        className="rounded-full"
      />
    ),
  },
  {
    dataIndex: 'birthday',
    title: '生日',
    key: 'birthday',
    width: 80,
    align: 'center',
  },
  {
    dataIndex: 'email',
    title: '邮箱',
    key: 'email',
    width: 80,
    align: 'center',
  },
  {
    dataIndex: 'status',
    title: '状态',
    key: 'status',
    width: 60,
    align: 'center',
    render: (text: number) => {
      return text === 1 ? (
        <Tag color="green">正常</Tag>
      ) : (
        <Tag color="gray">冻结</Tag>
      );
    },
  },
  {
    title: '操作',
    width: '10%',
    dataIndex: 'action',
    fixed: 'right',
    align: 'center',
    render: (_: any, record: UserModel) => (
      <Space size={0}>
        <Button type="link" size="small" onClick={() => handleDetail(record)}>
          详情
        </Button>
        <Button type="link" size="small" onClick={() => handleEdit(record)}>
          编辑
        </Button>
        <Dropdown
          menu={{ items: handleMore(record) }}
          placement="bottom"
          trigger={['click']}
        >
          <Button type="link" size="small" icon={<MoreOutlined />} />
        </Dropdown>
      </Space>
    ),
  },
];
